query.pigql:
q = load "${App.Datasets.opportunity_PartOne.Alias}";
q1 = filter q by {{cell(timeperiod_list.selection, 0, "prior").asDateRange("date('CreatedDate_Year', 'CreatedDate_Month', 'CreatedDate_Day')")}};
q2 = filter q by {{cell(timeperiod_list.selection, 0, "current").asDateRange("date('CreatedDate_Year', 'CreatedDate_Month', 'CreatedDate_Day')")}};
co = group q1 by all, q2 by all;
co = foreach co generate (sum(q2.'Amount')/sum(q1.'Amount')) as 'sum_percent';

q = load "${App.Datasets.opportunity_PartOne.Alias}";
q = filter q by 'IsWon' == "true";
q1 = filter q by {{cell(timeperiod_list.selection, 0, "prior").asDateRange("date('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day')")}};
q2 = filter q by {{cell(timeperiod_list.selection, 0, "current").asDateRange("date('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day')")}};
co = group q1 by all, q2 by all;
co = foreach co generate (sum(q2.'Amount')-sum(q1.'Amount'))/sum(q1.'Amount') as 'sum_percent'; co = foreach co generate 'sum_percent' as 'sum_percent', (case when 'sum_percent' < 0 then "#DC0029" when 'sum_percent' > 0 then "#26AB40" else "#7D98B3" end) as 'color';

q = load "${App.Datasets.opportunity_PartOne.Alias}";
q = filter q by 'Type' == "New Business";
q1 = filter q by {{cell(timeperiod_list.selection, 0, "current").asDateRange("date('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day')")}};
q2 = filter q by {{cell(timeperiod_list.selection, 0, "prior").asDateRange("date('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day')")}};
co = group q1 by all, q2 by all;
co = foreach co generate (sum(q1.'Amount')/coalesce(sum(q2.'Amount'), 1)) as 'sum_percent'
